changeSet.sql:
CREATE TABLE software_vendor
(
    id              int         NOT NULL auto_increment primary key,
    vendor_name     varchar(50) NOT NULL,
    language_used   varchar(20) NOT NULL
);

CREATE TABLE hardware_vendor (
    id              int         NOT NULL auto_increment primary key,
    vendor_name     varchar(50) NOT NULL,
    type_of_chip    varchar(50) NOT NULL
);
